function error_fun=err(params)   %params is the vector [w0, z0]global w_values z_values wavelengthparams(1)params(2)error_fun = sum( abs( sqrt(w_values.^2 - params(1).^2).*pi.*params(1) ./ ...                      wavelength - abs(params(2)-z_values) ...                    ) + ...                 abs( w_values.^2 - (params(1).^2 + (wavelength./(pi.*params(1))).^2 .* ...                      (z_values-params(2)).^2) ...                    ) ...                );